INSERT INTO `notification_default_templates` (`id`, `code`, `subject`, `body`, `status`, `position`, `recipient`, `created_at`, `updated_at`) VALUES
(1,	'order_confirmation',	'Order #{{ order.id }} confirmation',	'<!DOCTYPE html>\r\n<html lang=\"en\">\r\n  <head>\r\n  <title>{{ email_title }}</title>\r\n  <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\">\r\n  <meta name=\"viewport\" content=\"width=device-width\">\r\n</head>\r\n\r\n  <body>\r\n    <table class=\"body\">\r\n      <tr>\r\n        <td>\r\n          <table class=\"header row\">\r\n  <tr>\r\n    <td class=\"header__cell\">\r\n      <center>\r\n\r\n        <table class=\"container\">\r\n          <tr>\r\n            <td>\r\n\r\n              <table class=\"row\">\r\n                <tr>\r\n                  <td class=\"shop-name__cell\">\r\n                    {%- if shop.email_logo_url %}\r\n                      <img src=\"{{shop.email_logo_url}}\" alt=\"{{ shop.name }}\" width=\"{{ shop.email_logo_width }}\">\r\n                    {%- else %}\r\n                      <h1 class=\"shop-name__text\">\r\n                        <a href=\"{{shop.url}}\">{{ shop.name }}</a>\r\n                      </h1>\r\n                    {%- endif %}\r\n                  </td>\r\n\r\n                    <td class=\"order-number__cell\">\r\n                      <span class=\"order-number__text\">\r\n                        Order {{ order_name }}\r\n                      </span>\r\n                    </td>\r\n                </tr>\r\n              </table>\r\n\r\n            </td>\r\n          </tr>\r\n        </table>\r\n\r\n      </center>\r\n    </td>\r\n  </tr>\r\n</table>\r\n\r\n          <table class=\"row content\">\r\n  <tr>\r\n    <td class=\"content__cell\">\r\n      <center>\r\n        <table class=\"container\">\r\n          <tr>\r\n            <td>\r\n\r\n            <h2>{{ email_title }}</h2>\r\n            <p>{{ email_body }}</p>\r\n            {% if order_status_url %}\r\n              <table class=\"row actions\">\r\n  <tr>\r\n    <td class=\"actions__cell\">\r\n      <table class=\"button main-action-cell\">\r\n        <tr>\r\n          <td class=\"button__cell\"><a href=\"{{ order_status_url }}\" class=\"button__text\">View your order</a></td>\r\n        </tr>\r\n      </table>\r\n      {% if shop.url %}\r\n    <table class=\"link secondary-action-cell\">\r\n      <tr>\r\n        <td class=\"link__cell\">or <a href=\"{{ shop.url }}\">Visit our store</a></td>\r\n      </tr>\r\n    </table>\r\n{% endif %}\r\n\r\n    </td>\r\n  </tr>\r\n</table>\r\n\r\n            {% else %}\r\n              {% if shop.url %}\r\n    <table class=\"row actions\">\r\n      <tr>\r\n        <td class=\"actions__cell\">\r\n          <table class=\"button main-action-cell\">\r\n            <tr>\r\n              <td class=\"button__cell\"><a href=\"{{ shop.url }}\" class=\"button__text\">Visit our store</a></td>\r\n            </tr>\r\n          </table>\r\n        </td>\r\n      </tr>\r\n    </table>\r\n{% endif %}\r\n\r\n            {% endif %}\r\n\r\n            </td>\r\n          </tr>\r\n        </table>\r\n      </center>\r\n    </td>\r\n  </tr>\r\n</table>\r\n\r\n          <table class=\"row section\">\r\n  <tr>\r\n    <td class=\"section__cell\">\r\n      <center>\r\n        <table class=\"container\">\r\n          <tr>\r\n            <td>\r\n              <h3>Order summary</h3>\r\n            </td>\r\n          </tr>\r\n        </table>\r\n        <table class=\"container\">\r\n          <tr>\r\n            <td>\r\n\r\n\r\n<table class=\"row\">\r\n  {% for line in subtotal_line_items %}\r\n  <tr class=\"order-list__item\">\r\n    <td class=\"order-list__item__cell\">\r\n      <table>\r\n        <td>\r\n          {% if line.image %}\r\n            <img src=\"{{ line | img_url: \'compact_cropped\' }}\" align=\"left\" width=\"60\" height=\"60\" class=\"order-list__product-image\"/>\r\n          {% endif %}\r\n        </td>\r\n        <td class=\"order-list__product-description-cell\">\r\n          {% if line.product.title %}\r\n            {% assign line_title = line.product.title %}\r\n          {% else %}\r\n            {% assign line_title = line.title %}\r\n          {% endif %}\r\n\r\n          {% if line.quantity < line.quantity %}\r\n            {% capture line_display %} {{ line.quantity }} of {{ line.quantity }} {% endcapture %}\r\n          {% else %}\r\n            {% assign line_display = line.quantity  %}\r\n          {% endif %}\r\n\r\n          <span class=\"order-list__item-title\">{{ line_title }} × {{ line_display }}</span><br/>\r\n\r\n          {% if line.variant.title != \'Default Title\' %}\r\n            <span class=\"order-list__item-variant\">{{ line.variant.title }}</span>\r\n          {% endif %}\r\n        </td>\r\n          <td class=\"order-list__price-cell\">\r\n            {% if line.original_line_price != line.line_price %}\r\n              <del class=\"order-list__item-original-price\">{{ line.original_line_price | money }}</del>\r\n            {% endif %}\r\n            <p class=\"order-list__item-price\">{{ line.line_price | money }}</p>\r\n          </td>\r\n      </table>\r\n    </td>\r\n  </tr>{% endfor %}\r\n</table>\r\n\r\n            <table class=\"row subtotal-lines\">\r\n  <tr>\r\n    <td class=\"subtotal-spacer\"></td>\r\n    <td>\r\n      <table class=\"row subtotal-table\">\r\n        {% if discounts %}\r\n          {% if discounts.first.code %}\r\n            Discount {{ discounts.first.code }}\r\n          {% else %}\r\n            Discount\r\n          {% endif %}\r\n\r\n<tr class=\"subtotal-line\">\r\n  <td class=\"subtotal-line__title\">\r\n    <p>\r\n      <span>{{ discount_title }}</span>\r\n    </p>\r\n  </td>\r\n  <td class=\"subtotal-line__value\">\r\n    <strong>{{ discounts_savings | money }}</strong>\r\n  </td>\r\n</tr>\r\n\r\n        {% endif %}\r\n\r\n\r\n<tr class=\"subtotal-line\">\r\n  <td class=\"subtotal-line__title\">\r\n    <p>\r\n      <span>Subtotal</span>\r\n    </p>\r\n  </td>\r\n  <td class=\"subtotal-line__value\">\r\n    <strong>{{ subtotal_price | money }}</strong>\r\n  </td>\r\n</tr>\r\n\r\n\r\n<tr class=\"subtotal-line\">\r\n  <td class=\"subtotal-line__title\">\r\n    <p>\r\n      <span>Shipping</span>\r\n    </p>\r\n  </td>\r\n  <td class=\"subtotal-line__value\">\r\n    <strong>{{ shipping_price | money }}</strong>\r\n  </td>\r\n</tr>\r\n\r\n\r\n        {% for line in tax_lines %}\r\n\r\n<tr class=\"subtotal-line\">\r\n  <td class=\"subtotal-line__title\">\r\n    <p>\r\n      <span>{{ line.title }}</span>\r\n    </p>\r\n  </td>\r\n  <td class=\"subtotal-line__value\">\r\n    <strong>{{ line.price | money }}</strong>\r\n  </td>\r\n</tr>\r\n\r\n        {% endfor %}\r\n\r\n        {% if total_tip and total_tip > 0 %}\r\n\r\n<tr class=\"subtotal-line\">\r\n  <td class=\"subtotal-line__title\">\r\n    <p>\r\n      <span>Tip</span>\r\n    </p>\r\n  </td>\r\n  <td class=\"subtotal-line__value\">\r\n    <strong>{{ total_tip | money }}</strong>\r\n  </td>\r\n</tr>\r\n\r\n        {% endif %}\r\n      </table>\r\n      <table class=\"row subtotal-table subtotal-table--total\">\r\n\r\n<tr class=\"subtotal-line\">\r\n  <td class=\"subtotal-line__title\">\r\n    <p>\r\n      <span>Total</span>\r\n    </p>\r\n  </td>\r\n  <td class=\"subtotal-line__value\">\r\n    <strong>{{ total_price | money_with_currency }}</strong>\r\n  </td>\r\n</tr>\r\n\r\n      </table>\r\n\r\n      {% assign transaction_size = 0 %}\r\n      {% for transaction in transactions %}\r\n        {% unless transaction.kind == \"capture\" or transaction.kind == \"void\" %}\r\n          {% assign transaction_size = transaction_size | plus: 1 %}\r\n        {% endunless %}\r\n      {% endfor %}\r\n\r\n      {% if transaction_size > 1 %}\r\n        <table class=\"row subtotal-table\">\r\n          <tr><td colspan=\"2\" class=\"subtotal-table__line\"></td></tr>\r\n          <tr><td colspan=\"2\" class=\"subtotal-table__small-space\"></td></tr>\r\n\r\n          {% for transaction in transactions %}\r\n            {% if transaction.status == \"success\" and transaction.kind == \"authorization\" or transaction.kind == \"sale\" %}\r\n              {% if transaction.payment_details.credit_card_company %}\r\n                {% capture transaction_name %}{{ transaction.payment_details.credit_card_company }} (ending in {{ transaction.payment_details.credit_card_last_four_digits }}){% endcapture %}\r\n              {% else %}\r\n                {% capture transaction_name %}{{ transaction.gateway | replace: \"_\", \" \" | capitalize }}{% endcapture %}\r\n              {% endif %}\r\n\r\n\r\n<tr class=\"subtotal-line\">\r\n  <td class=\"subtotal-line__title\">\r\n    <p>\r\n      <span>{{transaction_name}}</span>\r\n    </p>\r\n  </td>\r\n  <td class=\"subtotal-line__value\">\r\n    <strong>{{ transaction.amount | money }}</strong>\r\n  </td>\r\n</tr>\r\n\r\n            {% endif %}\r\n            {% if transaction.kind == \'refund\' %}\r\n              {% if transaction.payment_details.credit_card_company %}\r\n                {% assign refund_method_title = transaction.payment_details.credit_card_company %}\r\n              {% else %}\r\n                {% assign refund_method_title = transaction.gateway %}\r\n              {% endif %}\r\n\r\n\r\n<tr class=\"subtotal-line\">\r\n  <td class=\"subtotal-line__title\">\r\n    <p>\r\n      <span>Refund</span>\r\n        <br>\r\n        <small>{{ refund_method_title | capitalize }}</small>\r\n    </p>\r\n  </td>\r\n  <td class=\"subtotal-line__value\">\r\n    <strong>- {{ transaction.amount | money }}</strong>\r\n  </td>\r\n</tr>\r\n\r\n            {% endif %}\r\n          {% endfor %}\r\n        </table>\r\n      {% endif %}\r\n    </td>\r\n  </tr>\r\n</table>\r\n\r\n\r\n            </td>\r\n          </tr>\r\n        </table>\r\n      </center>\r\n    </td>\r\n  </tr>\r\n</table>\r\n\r\n          <table class=\"row section\">\r\n  <tr>\r\n    <td class=\"section__cell\">\r\n      <center>\r\n        <table class=\"container\">\r\n          <tr>\r\n            <td>\r\n              <h3>Customer information</h3>\r\n            </td>\r\n          </tr>\r\n        </table>\r\n        <table class=\"container\">\r\n          <tr>\r\n            <td>\r\n\r\n            <table class=\"row\">\r\n              <tr>\r\n                {% if requires_shipping and shipping_address %}\r\n                <td class=\"customer-info__item\">\r\n                  <h4>Shipping address</h4>\r\n                  {{ shipping_address | format_address }}\r\n                </td>\r\n                {% endif %}\r\n                {% if billing_address %}\r\n                <td class=\"customer-info__item\">\r\n                  <h4>Billing address</h4>\r\n                  {{ billing_address | format_address }}\r\n                </td>\r\n                {% endif %}\r\n              </tr>\r\n            </table>\r\n            <table class=\"row\">\r\n              <tr>\r\n                {% if requires_shipping and shipping_address %}\r\n                <td class=\"customer-info__item\">\r\n                  <h4>Shipping method</h4>\r\n                  <p>{{ shipping_method.title }}</p>\r\n                </td>\r\n                {% endif %}\r\n                {% assign transaction_count = transactions | size %}\r\n                {% if transaction_count > 0 %}\r\n                <td class=\"customer-info__item\">\r\n                  <h4>Payment method</h4>\r\n                  {% for transaction in transactions %}\r\n                    {% if transaction.status == \"success\" or transaction.status == \"pending\" %}\r\n                      {% if transaction.kind == \"authorization\" or transaction.kind == \"sale\" %}\r\n                        <p class=\"customer-info__item-content\">\r\n                          {% if transaction.payment_details.credit_card_company %}\r\n                            {% capture credit_card_url %}notifications/{{ transaction.payment_details.credit_card_company | downcase | replace: \" \", \"_\" }}.png{% endcapture %}\r\n                            <img src=\"{{ credit_card_url | shopify_asset_url }}\" class=\"customer-info__item-credit\" height=\"24\">\r\n                            <span>Payment method — <strong>{{ total_price | money }}</strong></span>\r\n                            <span>Payment method — <strong>{{ total_price | money }}</strong></span>\r\n                          {% else %}\r\n                            {{ transaction.gateway | replace: \"_\", \" \" | capitalize }} — <strong>{{ transaction.amount | money }}</strong>\r\n                          {% endif %}\r\n                        </p>\r\n                      {% endif %}\r\n                    {% endif %}\r\n                  {% endfor %}\r\n                </td>\r\n                {% endif %}\r\n              </tr>\r\n            </table>\r\n\r\n            </td>\r\n          </tr>\r\n        </table>\r\n      </center>\r\n    </td>\r\n  </tr>\r\n</table>\r\n\r\n          <table class=\"row footer\">\r\n  <tr>\r\n    <td class=\"footer__cell\">\r\n      <center>\r\n        <table class=\"container\">\r\n          <tr>\r\n            <td>\r\n              <p class=\"disclaimer__subtext\">If you have any questions, reply to this email or contact us at <a href=\"mailto:{{ shop.email }}\">{{ shop.email }}</a></p>\r\n            </td>\r\n          </tr>\r\n        </table>\r\n      </center>\r\n    </td>\r\n  </tr>\r\n</table>\r\n\r\n<img src=\"{{ \'notifications/spacer.png\' | shopify_asset_url }}\" class=\"spacer\" height=\"1\" />\r\n\r\n        </td>\r\n      </tr>\r\n    </table>\r\n  </body>\r\n</html>\r\n\r\n                           ',	1,	1,	2,	0,	0),
(2,	'order_in_progress',	'Order #{{ order.id }} in progress',	'<!doctype html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width\">\r\n</head>\r\n<body>\r\n\r\n<table class=\"mail-block\">\r\n    <tbody>\r\n        <tr>\r\n            <td class=\"mail-block__body\">\r\n                <table class=\"mail-header\">\r\n                    <tbody>\r\n                    <tr>\r\n                        <td>\r\n                            <table class=\"mail-container mail-header__top\">\r\n                                <tbody>\r\n                                <tr>\r\n                                    <td>\r\n                                        {% if site.logo %}\r\n                                            <img src=\"{{ site.logo }}\" alt=\"\" class=\"img-responsive\">\r\n                                        {% else %}\r\n                                            <h2 class=\"mail-header__title\">{{ site.store_name }}</h2>\r\n                                        {% endif %}\r\n                                    </td>\r\n                                    <td class=\"text-right mail-header__top-order\">\r\n                                        Order #{{ order.id }}\r\n                                    </td>\r\n                                </tr>\r\n                                </tbody>\r\n                            </table>\r\n                        </td>\r\n                    </tr>\r\n                    </tbody>\r\n                </table>\r\n                <table class=\"mail-container mail-body\">\r\n                    <tbody>\r\n                    <tr>\r\n                        <td>\r\n                            <table class=\"mail-body__header text-center\">\r\n                                <tbody>\r\n                                <tr>\r\n                                    <td>\r\n                                        <span class=\"mail-body__header-title\">\r\n                                            Order #{{ order.id }} in progress\r\n                                        </span>\r\n                                    </td>\r\n                                </tr>\r\n                                <tr>\r\n                                    <td>\r\n                                        <span class=\"mail-body__header-description\">\r\n\r\n                                        </span>\r\n                                    </td>\r\n                                </tr>\r\n                                </tbody>\r\n                            </table>\r\n                            <table class=\"mail-body__link\">\r\n                                <tbody>\r\n                                <tr>\r\n                                    <td>\r\n                                        <a href=\"{{ order.url }}\" class=\"mail-visit-button\">View your order</a>\r\n                                    </td>\r\n                                </tr>\r\n                                <tr>\r\n                                    <td>\r\n                                        or\r\n                                    </td>\r\n                                </tr>\r\n                                <tr>\r\n                                    <td>\r\n                                        <a href=\"{{ site.url }}\" class=\"mail-visit-button-store\">Visit our store</a>\r\n                                    </td>\r\n                                </tr>\r\n                                </tbody>\r\n                            </table>\r\n                        </td>\r\n                    </tr>\r\n                    </tbody>\r\n                </table>\r\n                <table class=\"mail-footer\">\r\n                    <tbody>\r\n                    <tr>\r\n                        <td>\r\n                            <table class=\"mail-container mail-footer__body\">\r\n                                <tbody>\r\n                                <tr>\r\n                                    <td class=\"text-center\">\r\n                                        If you have any questions contact us at <a href=\"mailto:{{ site.admin_email }}\">{{ site.admin_email }}</a>\r\n                                    </td>\r\n                                </tr>\r\n                                </tbody>\r\n                            </table>\r\n                        </td>\r\n                    </tr>\r\n                    </tbody>\r\n                </table>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<style>\r\n    .html, body{\r\n        padding: 15px;\r\n        margin: 0px;\r\n        font-family: Arial, Helvetica, sans-serif;\r\n        font-size: 14px;\r\n        color: #4e4848;\r\n        background: #f7f7f7;\r\n    }\r\n    table{\r\n        border-spacing: 0;\r\n        border-collapse: collapse;\r\n    }\r\n    .mail-block{\r\n        background: #fff;\r\n        margin: 30px auto;\r\n        max-width: 700px;\r\n        box-shadow: 0px 1px 4px 0px #e6e6e6;\r\n        -webkit-box-shadow: 0px 1px 4px 0px #e6e6e6;\r\n        -moz-box-shadow: 0px 1px 4px 0px #e6e6e6;\r\n    }\r\n    .mail-block__body{\r\n        padding: 0px 30px;\r\n    }\r\n    .mail-container{\r\n        max-width: 768px;\r\n        min-width: 650px;\r\n        width: 100%;\r\n        margin: 0 auto;\r\n        padding: 0px 15px;\r\n    }\r\n    .text-right{\r\n        text-align: right;\r\n    }\r\n    .text-center{\r\n        text-align: center;\r\n    }\r\n    .mail-header{\r\n        height: 120px;\r\n        width: 100%;\r\n    }\r\n    .mail-footer{\r\n        height: 75px;\r\n        width: 100%;\r\n        font-size: 14px;\r\n        margin-top: 70px;\r\n    }\r\n    .mail-footer a{\r\n        color: #333;\r\n    }\r\n    .mail-body{\r\n        width: 100%;\r\n        margin-top: 30px;\r\n        margin-bottom: 30px;\r\n    }\r\n    .mail-footer__body{\r\n        max-width: 768px;\r\n        width: 100%;\r\n        margin: 0 auto;\r\n    }\r\n    .img-responsive{\r\n        display: block;\r\n        width: auto;\r\n        height: auto;\r\n    }\r\n    .mail-header__top{\r\n\r\n    }\r\n    .mail-header__top-order{\r\n        font-size: 18px;\r\n        color: #a9a9a9;\r\n    }\r\n    .mail-header__top img{\r\n        max-width: 250px;\r\n        display: inline-block;\r\n    }\r\n    .mail-body__content{\r\n        width: 100%;\r\n        border: 1px solid #ddd;\r\n        border-radius: 5px;\r\n    }\r\n    .mail-body__content td{\r\n        padding: 10px 10px;\r\n        border-bottom: 1px solid #ddd;\r\n        border-right: 1px solid #ddd;\r\n    }\r\n    .order-total{\r\n        font-weight: bold;\r\n    }\r\n    .order-total__title{\r\n        text-align: right;\r\n    }\r\n    .order-total__price{\r\n        font-size: 20px;\r\n        font-weight: bold;\r\n    }\r\n    .order-total-all{\r\n        font-size: 20px;\r\n        font-weight: bold;\r\n    }\r\n    .mail-body__caption{\r\n        font-size: 22px;\r\n        width: 100%;\r\n    }\r\n    .mail-body__caption{\r\n        border-top: 1px solid #ddd;\r\n        border-left: 1px solid #ddd;\r\n        border-right: 1px solid #ddd;\r\n    }\r\n    .mail-body__caption td{\r\n        padding: 10px;\r\n    }\r\n    .mail-body__header{\r\n        margin: 15px auto;\r\n        width: 100%;\r\n    }\r\n    .mail-body__header-title{\r\n        font-size: 28px;\r\n        font-weight: 600;\r\n    }\r\n    .mail-body__header-description{\r\n        display: block;\r\n        width: 100%;\r\n        color: #867979;\r\n        line-height: 1.5;\r\n    }\r\n    .mail-body__payment{\r\n        margin-top: 15px;\r\n        font-size: 18px;\r\n    }\r\n    .mail-body__payment-title{\r\n        font-weight: bold;\r\n    }\r\n    .mail-visit-button{\r\n        display: inline-block;\r\n        max-width: 350px;\r\n        width: 100%;